// App est en class

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      charactere: nelsonMuntz,

  }
}
  getSim() {
    // Send the request
    axios.get('https://thesimpsonsquoteapi.glitch.me/quotes')
      // Extract the DATA from the received response
      .then(response => response.data)

      // Use this data to update the state
      .then(data => {
        this.setState({
          charactere: data.results[0],
        });
    });
  }

  render() {
    return (
      <div className="App">
        <Navbar />
        <button type="button" onClick={this.Sim}>Get Quote</button>
        <QuoteCard charactere={nelsonMuntz} />
      </div>
    );
  }
}


export default App;
