import React from 'react';
import './NavBar.css';


const nelsonMuntz = {
    blockquote : 'Shoplifting is a victimless crime, like punching someone in the dark.',
    cite : "Nelson Muntz",
    }


function QuoteCards({charactere}) {


    return (
      <figure className="QuoteCard">
      <img src={charactere.image} alt={charactere.character} />
      <figcaption>
        <blockquote>{charactere.quote}</blockquote>
        <cite>{charactere.character}</cite>
      </figcaption>
    </figure>
    )



}

export default QuoteCards;
