import React from 'react';
import './NavBar.css';

const nelsonMuntz = {
    blockquote : 'Shoplifting is a victimless crime, like punching someone in the dark.',
    cite : "Nelson Muntz",
    }


function QuoteCards(props){
  return (
    <figure class="QuoteCard">
    <img
      src={props.link}
      alt={props.cite} />
    <figcaption>
      <blockquote>
        {props.blockquote}
      </blockquote>
      <cite>{props.cite}</cite>
    </figcaption>
  </figure>
  );




}

export default QuoteCards;
