    cite : "Nelson Muntz",
    }
const dah = "no"
const doh = "su";
function hey() {

  if (doh === "su"){

    return "Doh!!";
  }

}

class QuoteCards extends React.Component{
  constructor(sim) {
  super(sim);
  this.state = {
    charactere: nelsonMuntz,

  }
}
getSim() {
  // Send the request
  axios.get('https://thesimpsonsquoteapi.glitch.me/quotes')
    // Extract the DATA from the received response
    .then(response => response.data)

    // Use this data to update the state
    .then(data => {
      this.setState({
        charactere: data.results[0],
      });
  });
}
  render() {
    return (
      <figure className="QuoteCard">
      <img src={this.image} alt={this.cite} />
      <figcaption>
        <blockquote>{this.blockquote}</blockquote>
        <cite>{this.sim.cite}</cite>
      </figcaption>
    </figure>
    )
  }


}

export default QuoteCards;
