import React from 'react';
import './App.css';
import Navbar from './components/Navbar';
import axios from 'axios';
//import QuoteCards from './components/QuoteCard';
//import nelsonMuntz from  './components/QuoteCard';
import DisplayEmployee from './components/DisplayEmployee';
// const pour faire une fausse carte en dure au cas ou l'api galère. et charger quelque chose.
const sampleEmployee = {
  gender: 'male',
  name: {
    first: 'Charlie',
    last: 'Thompson',
  },
  location: {
    street: {
      number: 761,
      name: 'Tay Street',
    },
    city: 'Timaru',
    postcode: 76111,
  },
  email: 'charlie.thompson@example.com',
  picture: {
    medium: 'https://randomuser.me/api/portraits/med/men/40.jpg',
  },
};
// App est en class

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      employee: sampleEmployee
    };
    this.getEmployee = this.getEmployee.bind(this);
  }
  getEmployee() {
    // Send the request
    axios.get('https://randomuser.me/api?nat=fr')
      // Extract the DATA from the received response
      .then(response => response.data)
      // Use this data to update the state
      .then(data => {
        this.setState({
          employee: data.results[0],
        });
    });
  }
  render() {
    return (
      <div className="App">

        <button type="button" onClick={this.getEmployee}>Get employee</button>
        <DisplayEmployee employee={this.state.employee} />
      </div>
    );
  }
}


export default App;
