import React from 'react';
import './NavBar.css';

const nelsonMuntz = {
    blockquote : 'Shoplifting is a victimless crime, like punching someone in the dark.',
    cite : "Nelson Muntz",
    }
const doh = 1;
function hey() {

  if (doh === '1'){

      doh = "<p>Doh!!<p>";
  }

}


function QuoteCards(props){
    return (
    <figure class="QuoteCard">
    {hey()}
    <img
      src={props.link}
      alt={props.cite}
       />
    <figcaption>
      <blockquote>
        {props.blockquote}
      </blockquote>
      <cite>{props.cite}</cite>
    </figcaption>
  </figure>
  );




}

export default QuoteCards;
